mapper.select:
select 

from tb_product_spu
where id = #{id,jdbcType=BIGINT}
  and tb_product_category_id = #{tbProductCategoryId,jdbcType=BIGINT}
  and tb_product_brand_id = #{tbProductBrandId,jdbcType=INTEGER}

mapper.delete:
delete from tb_product_spu
where id = #{id,jdbcType=BIGINT}
  and tb_product_category_id = #{tbProductCategoryId,jdbcType=BIGINT}
  and tb_product_brand_id = #{tbProductBrandId,jdbcType=INTEGER}
